mapper.select:
SELECT DOC_DATE, FORM_NAME, DOC_TITLE, DOC_NO, DOC_STATUS FROM APP_DOC WHERE MEMBER_NUM = #{member_num}
AND DOC_STATUS = #{doc_status}
AND DOC_STATUS != '임시'
ORDER BY DOC_NO DESC

SELECT COUNT(*) FROM APP_DOC WHERE MEMBER_NUM = #{member_num}
AND DOC_STATUS = '${doc_status}'
AND DOC_STATUS != '임시'

mapper.update:

UPDATE APPROVAL 
SET 
	APP_STATUS = #{app_status} 
WHERE 
	DOC_NO = #{doc_no} 
	AND MEMBER_NUM = #{member_num} 
	AND APP_LEVEL = #{app_level}

UPDATE 
	APPROVAL 
SET 
	APP_STATUS = #{app_status}, 
	APP_DATE = #{app_date}

	, REJ_REASON = #{rej_reason}

WHERE 
	DOC_NO = #{doc_no} 
AND 
	MEMBER_NUM = #{member_num}

UPDATE 
	APP_DOC 
SET 
	DOC_STATUS = #{doc_status} 
WHERE 
	DOC_NO = #{doc_no}
