mapper.update:

UPDATE APPROVAL 
SET 
	APP_STATUS = #{app_status} 
WHERE 
	DOC_NO = #{doc_no} 
	AND MEMBER_NUM = #{member_num} 
	AND APP_LEVEL = #{app_level}

UPDATE 
	APPROVAL 
SET 
	APP_STATUS = #{app_status}, 
	APP_DATE = #{app_date}

	, REJ_REASON = #{rej_reason}

WHERE 
	DOC_NO = #{doc_no} 
AND 
	MEMBER_NUM = #{member_num}

UPDATE 
	APP_DOC 
SET 
	DOC_STATUS = #{doc_status} 
WHERE 
	DOC_NO = #{doc_no}
