mapper.select:
select password from member 
where member_num = #{member_num}
and member_name = #{member_name}
and ssn = #{ssn}

mapper.update:
UPDATE member
SET phone = #{phone}, mail = #{mail}
WHERE member_num = #{member_num}

 
